mapper.select:
select
	r_seq, seq, create_userid, content, create_datetime, update_datetime
	from reply where seq = #{seq}

mapper.update:
update reply set
	create_userid = #{create_userid},
	content = #{content}
where r_seq = #{r_seq}
	and seq = #{seq}

mapper.delete:
delete from reply
where r_seq = #{r_seq}
	and seq = ${seq}
